package irao;

public class Ique9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		for (; i <= 3; i++)  //0 -
		{
			if (i++ % 2 == 0) 
			{
				System.out.println("i = " + i);
			}
		}
		System.out.println("i after the loop = " + i);
		System.out.println(0%2);
		System.out.println( -2%2);
	}
}
